export declare type Nullable<T> = T | null;
export declare function get<T>(x: Nullable<T>): T;
export declare function orElseThrow<T>(x: Nullable<T>, err: Error): T;
export declare function orElse<T>(x: Nullable<T>, defaultVal: T): T;
export declare function isNull(x: any): x is null;
export declare function isNotNull<T>(x: T): x is T;
export declare function isUndefined(x: any): x is undefined;
export declare function isDefined<T>(x: T): x is T;
export declare function isNullOrUndefined(x: any): x is undefined | null;
export declare function isNotNullorUnDefined<T>(x: T): x is T;
